import './App.css';
import { useState, useEffect } from 'react';
import Login from "./components/Login"
import Register from './components/Register';

function App() {
  const [user, setUser] = useState()
  return (
    <div className="App">
      <Login newUser={setUser}/>
      {user
        ?
        <h2>Welcome {user}</h2>
        :
        <h2>Please log in</h2>
      }
      <Register newUser={setUser}/>
    </div>
  );
}

export default App;
